//////////// Animation state machine //////////
#define FRAME_SIZE 636

enum anim_state_t {
    IDLE_0,
    IDLE_1,
    IDLE_2,
    IDLE_3,
    IDLE_4,
    PAUSE,
    TAP_0,
    TAP_1,

    NUM_STATES
};

enum anim_state_t anim_state = IDLE_0;

void idle_event(void) {
    switch (anim_state) {
        case IDLE_0:
        case IDLE_1:
        case IDLE_2:
        case IDLE_3:
            anim_state++;
            break;
        case IDLE_4:
        case PAUSE:
        default:
            anim_state = IDLE_0;
            break;
        case TAP_0:
        case TAP_1:
            anim_state = PAUSE;
            break;
   }
}

void keypress_event(void) {
    switch (anim_state) {
        case IDLE_0:
        case IDLE_1:
        case IDLE_2:
        case IDLE_3:
        case PAUSE:
        case TAP_1:
        default:
            anim_state = TAP_0;
            break;
        case TAP_0:
            anim_state = TAP_1;
            break;
   }
}

